mapper.insert:
insert into t_bill_cost (id, t_bill_id, t_bill_price_id, 
  cost_amount, cost_date, ranking
  )
values (#{id,jdbcType=BIGINT}, #{tBillId,jdbcType=BIGINT}, #{tBillPriceId,jdbcType=BIGINT}, 
  #{costAmount,jdbcType=DECIMAL}, #{costDate,jdbcType=DATE}, #{ranking,jdbcType=INTEGER}
  )

mapper.select:
select

from t_bill_cost
where t_bill_id = #{BillId,jdbcType=BIGINT} AND cost_date=#{date,jdbcType=DATE}

select SUM(cost_amount)
 from t_bill_cost
  where
  year(cost_date) =#{year,jdbcType=INTEGER}
   and month(cost_date) =#{month,jdbcType=INTEGER}
  AND t_bill_id = #{billId,jdbcType=BIGINT}
  AND t_bill_price_id=#{billPriceId,jdbcType=BIGINT}
